$( function() {
    $( "#slider" ).slider();
} );

//정규식을 활용하여 3자리마다 콤마 삽입
var numberWithCommas = function (val) {
    while (/(\d+)(\d{3})/.test(val.toString())) {
        val = val.toString().replace(/(\d+)(\d{3})/, '$1' + ',' + '$2');
    }
    return val;
};


$(function() {
    $( "#slider-range" ).slider({
        range: true,
        min: 0,
        max: 10000000,
        step: 1000000,
        values: [ 0,1000000 ],
        slide: function( event, ui ) {
            $( "#amount").val(numberWithCommas(ui.values[ 0 ]) + "원 - " + numberWithCommas(ui.values[ 1 ]) + "원" );

            var pricerangestr = ui.values[ 0 ];
            var pricerangeend = ui.values[ 1 ];

            $('#pricerangestr').val(pricerangestr);
            $('#pricerangeend').val(pricerangeend);
        }
    });
    $.numberWithCommas( "#amount" ).val(numberWithCommas($( "#slider-range" ).slider( "values", 0 )) +
        "원 - " + numberWithCommas($( "#slider-range" ).slider( "values", 1 )) + "원");



});
